class SignupComponent extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            displayName:"",
            email:"",
            password:"",
            confirmPassword:"",
        }
    }
    render() {
        return(
            <div className="sign-up">
                <h2 className="title">I don't have an account.</h2>
                <span>Sign Up with your email and password</span>
            </div>
        )
    }
}
export default SignupComponent;
