import React from "react";
import {connect} from "react-redux";
import CollectionItemsComponent from "../../components/collection-items/CollectionItemsComponent";
import {covertCollectionSnapshotToMap, firestore} from "../../firebase/firebase.util";
import {updateCollections} from "../../redux/shop/shopAction";


const mapStateToProps=state=>({
    collections:state.shop.collections
});
/*const mapDispatchToProps=dispatch=>({
    updateCollections:(collectionMap)=>dispatch(updateCollections(collectionMap))
});*/
class CategoryPage extends React.Component {
    state={
        items:[],
        categoryCollections:this.props.collections,
    }
    unsbuscribeFromSnapshot=null;
    componentDidMount() {


        console.log(this.props.collections);

    }
    render() {
        const {updateCollections}=this.props;
        const collectionRef=firestore.collection('collections');
        this.unsbuscribeFromSnapshot=collectionRef.onSnapshot(async snapshot=>{
            const collectionsMap=covertCollectionSnapshotToMap(snapshot);
            updateCollections(collectionsMap)
        });
        return(
            <div className="collection-page">
                {/*<h2 className="title">{categoryCollections[0].title.toUpperCase()}</h2>
                <div className="items">
                    {items.map(item=><CollectionItemsComponent key={item.id} item={item}/>)}
                </div>*/}
            </div>
        );

}
}

export default connect(mapStateToProps,mapDispatchToProps)(CategoryPage);
