import React from "react";
import {connect} from "react-redux";
import CheckoutItemsComponent from "../../components/checkout-items/CheckoutItemsComponent";


const mapStateToProps =state=>({
    cartItems:state.cart.cartItems
});
class CheckOutPage extends React.Component{
    checkoutItems=cartItem=>(
        <div className="cart-item">
            <img src={cartItem.imageUrl} alt="item"/>
            <div className="item-details">
                <span className="name">{cartItem.name}</span>
                <span className="price">{cartItem.quantity}x{cartItem.price}Tk</span>
            </div>
        </div>
    );
    render() {
        return(
            <div className="checkout-page">
                <div className="checkout-header">
                    <div className="header-block">
                        <span>Product</span>
                    </div>
                    <div className="header-block">
                        <span>Description</span>
                    </div>
                    <div className="header-block">
                        <span>Quantity</span>
                    </div>
                    <div className="header-block">
                        <span>Price</span>
                    </div>
                    <div className="header-block">
                        <span>Remove</span>
                    </div>
                </div>
                {this.props.cartItems.map(cartItem=>(this.checkoutItems(cartItem)))}
                <div className="total">
                    <span>TOTAL: {this.props.cartItems.reduce((accumulateTotal,cartItem)=>
                        accumulateTotal+(cartItem.price*cartItem.quantity),0)}TK</span>
                </div>
            </div>
        )
    }



}

export default connect(mapStateToProps)(CheckOutPage);
