import React from "react";
const FormInput=({label,type,name,id,value,handleChange})=>{
    console.log(otherProps);
    return(
        <div className="group">
            <input className="form-input" type={type} onChange={handleChange}/>
            {
                label?(<label className={`${value.length?'shrink':""}form-input-label`}>
                    {label}
                </label>):null
            }
        </div>
    )
}
export default FormInput;
