import React from "react";
import CustomButton from "../custom-button/CustomButtonComponent";
import CartItem from "../cart-item/CartItem";
import {connect}from "react-redux";
import {addCartItems} from "../../redux/cart/cartActions";

const CartDropdownComponent=(props)=>{
    const {cartItems}=props;
    return(
        <div className="cart-dropdown">
            <div className="cart-items">
                <div>
                    {cartItems.map(cartItem=><CartItem key={cartItem.id} items={cartItem}/>)}
                </div>
                <CustomButton>GO TO CHECKOUT</CustomButton>
            </div>
        </div>
    )
};

const mapStateToProps=state=>({
    cartItems:state.cart.cartItems
});
export default connect(mapStateToProps)(CartDropdownComponent);
