import React from "react";
import {connect} from "react-redux";
import {removeItemFromCart} from "../../redux/cart/cartActions";

const CheckoutItemsComponent=({cartItem})=>{
    const {name,quantity,imageUrl,price}=cartItem;
    return(
      <div className="checkout-item">
          <div className="image-container">
              <img src={imageUrl} alt=""/>
          </div>
          <span className="name">{name}</span>
          <span className="quantity">{quantity}</span>
          <span className="price">{price}</span>
          <span className="remove-button"
                onClick={(cartItem)=>removeItemFromCart(cartItem)}>&#10005;
          </span>
      </div>
    )
};
const mapDispatchToProps=dispatch=>({
    clearItem:cartItem=>dispatch(removeItemFromCart(cartItem))
});
export default connect(null,mapDispatchToProps)(CheckoutItemsComponent);
