import React from "react";
import {Route} from "react-router-dom";
import CategoryPage from "../category/CategoryPage";
import {firestore,covertCollectionSnapshotToMap} from "../../firebase/firebase.util";
import {connect} from "react-redux";
import CollectionOverviewComponent from "../../components/collection-overview/CollectionOverviewComponent";
import {updateCollections} from "../../redux/shop/shopAction";

const mapDispatchToProps=dispatch=>{
    return{
        updateCollections:(collectionsMap)=>dispatch(updateCollections(collectionsMap))
    }
}
class ShopPage extends React.Component{
    unsbuscribeFromSnapshot=null;
    componentDidMount() {
        const {updateCollections}=this.props;
        const collectionRef=firestore.collection('collections');
        this.unsbuscribeFromSnapshot=collectionRef.onSnapshot(async snapshot=>{
            const collectionsMap=covertCollectionSnapshotToMap(snapshot);
            updateCollections(collectionsMap)
             /*snapshot.docs.map(docSnapshot=>{
                 const {title,items}=docSnapshot.data();
                 const collectionData={
                     routeName:encodeURI(title.toLowerCase()),
                     id:docSnapshot.id,
                     title,
                     items
                 }
                 accumulator[title.toLowerCase()]=collectionData;
                 updateCollections(accumulator);
             });*/

        })
    }

    render() {
        const {match}=this.props;
        return (
            <div className="shop-page">
                <Route exact path={`${match.path}`} component={CollectionOverviewComponent}/>
                <Route exact path={`${match.path}/:categoryId`} component={CategoryPage}/>
            </div>
        );
    }
}

export default connect(null,mapDispatchToProps)(ShopPage);
